select * from customer limit 20;

--Q1. What is the total revenue generated by male vs. female customers?
create view total_revenue as
select gender, sum(purchase_amount) as revenue from customer
group by gender;
select * from total_revenue

--Q2. Which customers used a discount but still spent more than the average purchase amount? 
create view more_than_purchase_amount as
select customer_id, purchase_amount from customer 
where discount_applied='Yes' and purchase_amount>=(select avg(purchase_amount) from customer);
select * from more_than_purchase_amount;


-- Q3. Which are the top 5 products with the highest average review rating?
select * from customer;
create view highest_rated_products as
select item_purchased, round(avg(review_rating::numeric),2) as"Average Product Rating"
from customer 
group by item_purchased
order by avg(review_rating) desc
limit 5;
select * from highest_rated_products;

--Q4. Compare the average Purchase Amounts between Standard and Express Shipping. 
select * from customer;
create view Shipping_comparision as
select shipping_type, round(avg(purchase_amount::numeric),2) as "Average Purchase Amount" from customer
where shipping_type in ('Standard', 'Express')
group by shipping_type;
select * from shipping_comparision;

--Q5. Do subscribed customers spend more? Compare average spend and total revenue 
--between subscribers and non-subscribers.
select * from customer;
create view subscribed_customer as
select subscription_status, count(customer_id) as "Total_customers",
round(avg(purchase_amount),2) as "avg_spend",
round(sum(purchase_amount),2) as "total_revenue" from customer
group by subscription_status order by total_revenue,avg_spend desc;
select * from subscribed_customer;

--Q6. Which 5 products have the highest percentage of purchases with discounts applied?
select * from customer;
create view highest_discounts as
select item_purchased as products, round(100* sum(case when discount_applied='Yes' then 1 else 0 end)/count(*),2)
as "discount_rate" from customer
group by item_purchased
order by discount_rate desc
limit 5;
select * from highest_discounts;


--Q7. Segment customers into New, Returning, and Loyal based on their total 
-- number of previous purchases, and show the count of each segment. 
select * from customer;
create view customer_type as
with customer_type as(
select customer_id, previous_purchases,
case
when previous_purchases=1 then 'New'
when previous_purchases between 2 and 10 then 'Returning'
else 'Loyal'
end as customer_segment
from customer
)
select customer_segment, count(*) as "Number Of Customers"
from customer_type
group by customer_segment;
select * from customer_type;

--Q8. What are the top 3 most purchased products within each category? 
select * from customer;
create view top_3_purchased as
with item_counts as(
select category, item_purchased, count(customer_id) as total_orders,
row_number() over(partition by category order by count(customer_id) desc) as item_rank
from customer
group by category, item_purchased)
select item_rank, category, item_purchased, total_orders from item_counts 
where item_rank<=3;
select * from top_3_purchased;
 
--Q9. Are customers who are repeat buyers (more than 5 previous purchases) also likely to subscribe?
create view subscribed_previous_buyers as
select subscription_status, count(customer_id) as repeat_buyers
from customer where previous_purchases>5
group by subscription_status;
select * from subscribed_previous_buyers;

--Q10. What is the revenue contribution of each age group? 
select * from customer;
create view revenue_contri as
select age_group, sum(purchase_amount) as total_revenue
from customer
group by age_group
order by total_revenue desc;
select * from revenue_contri;